package com.akiko23.impl.data

import com.akiko23.impl.data.models.WolfQuote
import kotlin.random.Random

/**
 * Репозиторий для получения волчьих цитат и картинок.
 */
internal class DeviceRepository {
    
    private val wolfQuotes = listOf(
        "Волк не тот кто волк, а тот кто волк",
        "Делай, как надо. Как не надо, не делай.",
        "Никогда не сдавайтесь, идите к своей цели! А если будет сложно – сдавайтесь.",
        "Жи-ши пиши от души.",
        "Главное не тот, кто прав а тот, кто лев",
        "Брат, помни, брат. А то я забыл",
    )
    
    private val wolfImages = listOf(
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTS0QqhiSdaqoUeuQz211h6rikcb_UoNjrtxA&s",
        "https://masterpiecer-images.s3.yandex.net/306ef9e24f0c11ee8a7a6a0259d7362a:upscaled",
        "https://i.guim.co.uk/img/media/1e3e5e2f6f3501befb629560ffe172a54ce3f016/139_98_2145_1287/master/2145.jpg?width=465&dpr=1&s=none&crop=none",
        "https://i1.sndcdn.com/artworks-tlzAzqBvY63Xu5Dd-efppZw-t1080x1080.png",
        "data:image/jpeg;base64,",
        "https://avatars.dzeninfra.ru/get-zen_doc/10893775/pub_65115a18dfe02a15fac53596_65115b82d2d11401e2199977/scale_1200",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQekRBzNyyXttBzjUJTse-8n1YAfM368SjHyA&s",
    )

    fun getRandomWolfQuote(): WolfQuote {
        val quote = wolfQuotes.random(Random)
        val imageUrl = wolfImages.random(Random)
        return WolfQuote(
            text = quote,
            imageUrl = imageUrl
        )
    }
}

